import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./Notes.css";
import {Button} from "@material-ui/core";
import DomainVerificationIcon from '@mui/icons-material/DomainVerification';
import BrushIcon from '@mui/icons-material/Brush';
import InsertPhotoIcon from '@mui/icons-material/InsertPhoto';


const Notes=()=>{
    const [title_style,settitle_style]=useState({
        display:"none",
    });

    const [title_value,settitle_value]=useState();

    const [note_value,setnote_value]=useState();

    const [values,setvalues]=useState([]);

    const [note_style2,setnote_style2]=useState({
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
    });

    // update properties on input in notes
    const TakeNote=()=>{
        settitle_style({
            display:"block",
        })
        setnote_style2({
            display:"none",
        })
    }

    // while entering notes in the input box 
    const Change=(event)=>{
        var {name,value}=event.target;
        if(name==="title"){
        settitle_value(value);
        setnote_value((pre)=>{
            return pre;
        })
        }
        else if(name==="note")
        {
        setnote_value(value);
        settitle_value((pre)=>{
            return pre;
        })
        }
    }

    // onclicking close after taking notes
    const Close=()=>{
        console.log("clicked");
        settitle_style({
            display:"none",
        })
        setnote_style2({
            display:"flex",
            justifyContent:"center",
            alignItems:"center",
        })
    }

    // this function adds the notes to the array of values
    const Add=()=>{
        setvalues((pre)=>{
            return [
                ...pre,{title_value,note_value}
            ]
        })
        setnote_value("");
        settitle_value("");
        console.log(values);
    }

    // removing a value from array of values
    const Delete=(index)=>{
        console.log("deleted");
        setvalues((pre)=>{
            return pre.filter((val,indexes)=>{
                return index!==indexes;
            })
        })
    }


    // JSX STARTS HERE
    return (
    <>
    <div className="notes-input-field bg-light mt-5" >
    <input  className="notes-title bg-light" type="text" placeholder="Title" style={title_style} onChange={Change} name="title" value={title_value}/>
    <div className="grid-view" style={{justifyContent:"center",alignContent:"center"}}>
    <input  className=" notes-note bg-light" type="text" placeholder="Take a Note.." onChange={Change} name="note" value={note_value} onClick={TakeNote}/>
    <div  style={note_style2}>
        <DomainVerificationIcon className="mx-3"/>
        <BrushIcon className="mx-3"/>
        <InsertPhotoIcon className="mx-3"/>
        </div>
        </div>
    
    
    <footer className="footer" style={title_style}>
        <Button onClick={Close} className="text-capitalize pos-right">close</Button>
        <Button className="bg-success" onClick={Add}>+</Button>
        
    </footer>
    </div>
    <div className="storage">
        
           { values.map((val,index)=>{
            return (
                    <>
                    <div className="card m-5">
                     <h3>{val.title_value} <Button className="text-capitalize position" style={{minWidth:"20px", margin:"auto",float:"right"}} onClick={()=>Delete(index)}></Button></h3>
            <p>{val.note_value}</p>
            </div>
                    </>
            )
            })
            }
           
        </div>
    </>
    );
}
export default Notes;
