import React, { useEffect, useState } from "react";
import Ques from "./Questions";
import { useNavigate } from "react-router-dom";
const Subject =()=>{
    const navigate=useNavigate();
    const [Subjects,setSubject]=useState([]);
    useEffect(()=>{

setSubject(()=>
    {
        return Array.from(new Set(Ques.map((val)=>
        {return val.subject}))
                        )
    }
        )
    },[])
    return (
        <>
        <div style={{backgroundImage:"Images",width:"fit-content",margin:"auto",height:"100vh",display:"grid",alignItems:"center"}}>
            <div>
       {Subjects.map((val,index)=>{
        return <h2 key={index} 
        style={{width:"350px",margin:"10px auto",borderRadius:"20px",backgroundColor:"yellowgreen",padding:"1%",textAlign:"center",cursor:"pointer"}}
        onClick={()=>{navigate('/Quiz',{state:{subj:val}})}}>
            {val}</h2>
       })}
       </div>
       </div>
        </>
    )
    
}
export default Subject;
