import React, { useEffect, useState } from 'react';
import Ques from './Questions';
import { useLocation } from 'react-router-dom';

const Quiz = () => {
  const [x, setx] = useState(0);
  const [Question, setquestion] = useState([]);
  const [Length, setlength] = useState(0);
  const [score, setscore] = useState(0);
  const [option, setoption] = useState();
  const [result, setresultdisplay] = useState(false);
  const [flag, setFlag] = useState([]);

  const subject = useLocation();

  useEffect(() => {
    console.log(subject.state.subj);
    setquestion(() => {
      return Ques.filter((val) => {
        return val.subject === subject.state.subj;
      });
    });
  }, []);

  useEffect(() => {
    console.log(Question.length);
    setlength(Question.length);
    setFlag(new Array(Question.length).fill(false));
  }, [Question]);

  const handleNext = (index) => {
    setx(x + 100);
    if (flag[index] === false && option === Question[index].answer) {
      setscore(score + 1);
      setFlag((prevFlags) => {
        const newFlags = [...prevFlags];
        newFlags[index] = true;
        return newFlags;
      });
    }
    else if(flag[index]===true){
      setscore(option===Question[index].answer?score:score-1)
    }
    setoption('');
  };

  return (
    <div>
      <div
        style={{
          overflow: 'hidden',
          display: !result ? 'flex' : 'none',
          width: '350px',
          margin: 'auto',
        }}
      >
        {Question.map((val, index) => (
          <div
            key={index}
            style={{
              minWidth: '100%',
              transform: `translateX(-${x}%)`,
              padding: '0% 1%',
            }}
          >
            <h3>{index + 1}. &nbsp;{val.question}</h3>
            <p
              className='opt'
              style={{
                backgroundColor: option === 'option1' ? 'green' : 'white',
              }}
              onClick={() => {
                setoption('option1');
              }}
            >
              (a)&emsp;{val.option1}
            </p>
            <p
              className='opt'
              style={{
                backgroundColor: option === 'option2' ? 'green' : 'white',
              }}
              onClick={() => {
                setoption('option2');
              }}
            >
              (b)&emsp;{val.option2}
            </p>
            <p
              className='opt'
              style={{
                backgroundColor: option === 'option3' ? 'green' : 'white',
              }}
              onClick={() => {
                setoption('option3');
              }}
            >
              (c)&emsp;{val.option3}
            </p>
            <p
              className='opt'
              style={{
                backgroundColor: option === 'option4' ? 'green' : 'white',
              }}
              onClick={() => {
                setoption('option4');
              }}
            >
              (d)&emsp;{val.option4}
            </p>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              {index !== 0 && (
                <button className='btn btn-primary' onClick={() => setx(x - 100)}>
                  prev
                </button>
              )}
              {index !== Length - 1 ? (
                <button className='btn btn-success' onClick={() => handleNext(index)}>
                  next
                </button>
              ) : (
                <button
                  className='btn btn-success'
                  style={{ float: 'right' }}
                  onClick={() => {
                    if (flag[index] === false && val.answer === option) {
                      setscore(score + 1);
                      setFlag((prevFlags) => {
                        const newFlags = [...prevFlags];
                        newFlags[index] = true;
                        return newFlags;
                      });
                    }
                    setresultdisplay(true);
                    setoption('');
                  }}
                >
                  submit
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
      <div className='result' style={{ display: result ? 'block' : 'none' }}>
        <p>you have scored {score} out of {Length}</p>
        <h2>
          {score / Length > 0.8
            ? ` congratulations you got ${(score / Length) * 100}%`
            : ' study to achieve a better result'}
        </h2>
      </div>
    </div>
  );
};

export default Quiz;
