import React from "react";
import "./Enquiry.css"
const pops=()=>{
    alert("your form has been submitted successfully");
    return true;
}
const Enquiry=()=>{
    return(
<>
<div className="enquiry-container">
<aside className="right-aside">
    <fieldset>
        <form target="_blank" onSubmit={()=>pops()}>
            <div>enter your name</div>
            <input className="input-field"  type="text" placeholder="enter your name" required="true"></input>
            <br/>
            <br/>
            <div>enter your email</div>
            <input className="input-field"  type="email" placeholder="ex:abcy@gmail.com" required="true"></input>
            <br/>
            <br/>
            <div>enter your phone number</div>
            <input  className="input-field" type="text" placeholder="enter your phone number"></input>
            <br/>
            <br/>
            <div>leave the message here</div>
            <textarea rows="10" cols="20" required="true" placeholder="write your query here"></textarea>
            <br/>
            <br/>
            <input type="submit" className="btn"></input>
            &emsp;
            <input type="reset" className="btn"></input>
        </form>
    </fieldset>
</aside>
</div>
</>        
    );
}
export default Enquiry;
