import React, { useEffect, useState } from 'react'
import Ques from './Questions';
import { NavLink, Route, Routes } from 'react-router-dom';
import MainPage from "./MainPage";
const Quiz = (props) => {
  const [x,setx]=useState(0);
    const [Question,setquestion]=useState([]);
    const [Length,setlength]=useState(0);
    const [score,setscore]=useState(0);
    // const [option,setoption]=useState();
    const [result,setresult]=useState(false);
    const[pre,setpre]=useState(false);
    const flag=[];
    const option=[];
    useEffect(()=>{
        setquestion(()=>{
            return Ques.filter((val)=>{
                return val.subject===props.subj;
            })
        })
    },[props])
    useEffect(()=>{
      console.log(Question.length);
      setlength(Question.length)
    },[Question])
  return (
    <div>
        <div   style={{overflow:"hidden",display:!result?"flex":"none",width:"350px",margin:"auto"}}>
      {Question.map((val,index)=>{
        if(index!==Length-1){
          return (
        <>
        <div style={{minWidth:"100%",transform:`translateX(-${x}%)`,padding:"1%"}}>
        <h3>{index+1}. &nbsp;{val.question}</h3>
            <p className='opt' style={{backgroundColor:option==="option1"?"green":"white"}} onClick={()=>{option[index]="option1"; flag[index]=false; if(pre===true)setscore(score-1)}}>(a)&emsp;{val.option1}</p>
            <p className='opt' style={{backgroundColor:option==="option2"?"green":"white"}} onClick={()=>{option[index]="option2"; flag[index]=false; if(pre===true)setscore(score-1)}}>(b)&emsp;{val.option2}</p>
            <p className='opt' style={{backgroundColor:option==="option3"?"green":"white"}} onClick={()=>{option[index]="option3"; flag[index]=false; if(pre===true)setscore(score-1)}}>(c)&emsp;{val.option3}</p>
            <p className='opt' style={{backgroundColor:option==="option4"?"green":"white"}} onClick={()=>{option[index]="option4"; flag[index]=false; if(pre===true)setscore(score-1)}}>(d)&emsp;{val.option4}</p>
            <div style={{display:"flex",justifyContent:"space-between"}}>
              <button className='btn btn-primary' onClick={()=>{setx(x-100)}}>prev</button>
            <button className='btn btn-success'onClick={()=>{
              setx(x+100);
               if(flag[index]!==true && option[index]===val.answer){setscore(x+1);
               flag[index]=true;
               setpre(true);
               }
               }}>next</button>
            </div>
            </div>
        </>
        )
        }
        else{
        return (
        <>
        <div style={{minWidth:"100%",transform:`translateX(-${x}%)`,padding:"1%"}}>
        <h3>{index+1}. &nbsp;{val.question}</h3>
           <p className='opt' style={{backgroundColor:option==="option1"?"green":"white"}} onClick={()=>{option[index]="option1"; flag[index]=false; setscore(score-1)}}>(a)&emsp;{val.option1}</p>
            <p className='opt' style={{backgroundColor:option==="option2"?"green":"white"}} onClick={()=>{option[index]="option2"; flag[index]=false; setscore(score-1)}}>(b)&emsp;{val.option2}</p>
            <p className='opt' style={{backgroundColor:option==="option3"?"green":"white"}} onClick={()=>{option[index]="option3"; flag[index]=false; setscore(score-1)}}>(c)&emsp;{val.option3}</p>
            <p className='opt' style={{backgroundColor:option==="option4"?"green":"white"}} onClick={()=>{option[index]="option4"; flag[index]=false; setscore(score-1)}}>(d)&emsp;{val.option4}</p>
            <button className='btn btn-primary' onClick={()=>{setx(x-100)}}>prev</button>
            <button className='btn btn-success' style={{float:"right"}} onClick={()=>{setscore(score+1); setresult(true)}}>submit</button>
            </div>
        </>
        )
        }
      })}
      </div>
      <div className='result' style={{display:result?"block":"none"}}>
        <p>you have scored {score} out of {Length}</p>
        <h2>
        {(score/Length)*100>80?` congratulations you got ${score/Length*100}%`:" study to achieve better result"}</h2>
        <NavLink to="/home"><button className='btn btn-primary' onClick={()=>{setx(x-100)}}>home</button></NavLink>
        <Routes><Route path='/home' Component={()=><MainPage/>}></Route></Routes>
            <button className='btn btn-success' style={{float:"right"}} onClick={()=>{setscore(0);setresult(false);setx(0); flag.map((val,index)=>{flag[index]=false; op})}}>try again</button>
      </div>
    </div>
  )
}

export default Quiz;
