import React, { useEffect, useState } from 'react'
import Ques from './Questions';
const Quiz = (props) => {
  const [x,setx]=useState(0);
    const [Question,setquestion]=useState([]);
    const [Length,setlength]=useState(0);
    const [score,setscore]=useState(0);
    const [option,setoption]=useState();
    const flag=[];
    useEffect(()=>{
        setquestion(()=>{
            return Ques.filter((val)=>{
                return val.subject===props.subj;
            })
        })
    },[props])
    useEffect(()=>{
      console.log(Question.length);
      setlength(Question.length)
    },[Question])
  return (
    <div>
        <div   style={{overflow:"hidden",display:"flex",width:"350px",margin:"auto"}}>
      {Question.map((val,index)=>{
        if(index!==Length-1){
          return (
        <>
        <div style={{minWidth:"100%",transform:`translateX(-${x}%)`,padding:"1%"}}>
        <h3>{index+1}. &nbsp;{val.question}</h3>
            <p id='opt1' onClick={()=>{setoption()}}>&emsp;{val.option1}</p>
            <p id='opt2' onClick={()=>{setoption()}}>&emsp;{val.option2}</p>
            <p id='opt3' onClick={()=>{setoption()}}>&emsp;{val.option3}</p>
            <p id='opt4' onClick={()=>{setoption()}}>&emsp;{val.option4}</p>
            <div style={{display:"flex",justifyContent:"space-between"}}>
              <button className='btn btn-primary' onClick={()=>{setx(x-100)}}>prev</button>
            <button className='btn btn-success'onClick={()=>{
              setx(x+100);
               if(flag[index]===false && option===val.answer){setscore(x+1);
               flag[index]=true;
               }
               }}>next</button>
            </div>
            </div>
        </>
        )
        }
        else{
        return (
        <>
        <div style={{minWidth:"100%",transform:`translateX(-${x}%)`,padding:"1%"}}>
        <h3>{index+1}. &nbsp;{val.question}</h3>
            <p id='opt1'><label htmlFor='opt1'><input type='radio' name={index} value='opt1'/></label>&emsp;{val.option1}</p>
            <p id='opt2'><label htmlFor='opt2'><input type='radio' name={index} value='opt2'/></label>&emsp;{val.option2}</p>
            <p id='opt3'><label htmlFor='opt3'><input type='radio' name={index} value='opt3'/></label>&emsp;{val.option3}</p>
            <p id='opt4'><label htmlFor='opt4'><input type='radio' name={index} value='opt4'/></label>&emsp;{val.option4}</p>
            <button className='btn btn-primary' onClick={()=>{setx(x-100)}}>prev</button>
            <button className='btn btn-success' style={{float:"right"}}>submit</button>
            </div>
        </>
        )
        }
      })}
      </div>
    </div>
  )
}

export default Quiz;
