import React, { useEffect, useRef, useState } from 'react'
import Ques from './Questions';
const Quiz = (props) => {
  const [x,setx]=useState(0);
  const form=useRef();
    console.log(props);
    const [Question,setquestion]=useState([]);
    useEffect(()=>{
        setquestion(()=>{
            return Ques.filter((val)=>{
                return val.subject===props.subj;
            })
        })
    },[props])
  return (
    <div>
        <form ref={form} onSubmit={(e)=>console.log(form.current)}  style={{overflow:"hidden",display:"flex",width:"350px",margin:"auto"}}>
      {Question.map((val,index)=>{
        return (
        <>
        <div style={{minWidth:"100%",trab}}>
        <h3>{val.question}</h3>
            <p id='opt1'><label htmlFor='opt1'><input type='radio' name={index} value='opt1'/></label>&emsp;{val.option1}</p>
            <p id='opt2'><label htmlFor='opt2'><input type='radio' name={index} value='opt2'/></label>&emsp;{val.option2}</p>
            <p id='opt3'><label htmlFor='opt3'><input type='radio' name={index} value='opt3'/></label>&emsp;{val.option3}</p>
            <p id='opt4'><label htmlFor='opt4'><input type='radio' name={index} value='opt4'/></label>&emsp;{val.option4}</p>
            <div style={{display:"flex",justifyContent:"space-between"}}>
            <button className='btn btn-primary'>prev</button>
            <button className='btn btn-success'>next</button>
            </div>
            </div>
        </>
        )
      })}
      <input type='submit'></input>
      </form>
    </div>
  )
}

export default Quiz;
