import React, { useEffect, useState } from 'react'
import Ques from './Questions';
const Quiz = (props) => {
    console.log(props);
    const [Question,setquestion]=useState([]);
    useEffect(()=>{
        setquestion(()=>{
            return Ques.filter((val)=>{
                return val.subject===props.subj;
            })
        })
    },[])
  return (
    <div>
      {Question.map((val)=>{
        return (
        <>
        <h3>{val.question}</h3>
        <ul>
            <label htmlFor='opt1'><input type='radio' name='opt' value='opt1'/></label><p id='opt1'>{val.option1}</p>
            <label htmlFor='opt2'><input type='radio' name='opt' value='opt2'/></label><p id='opt2'>{val.option2}</p>
            <label htmlFor='opt3'><input type='radio' name='opt' value='opt3'/></label><p id='opt3'>{val.option3}</p>
            <label htmlFor='opt4'><input type='radio' name='opt' value='opt4'/></label><p id='opt4'>{val.option4}</p>
        </ul>
        </>
        )
      })}
    </div>
  )
}

export default Quiz;
