import React from 'react'
import { useLocation } from 'react-router-dom'

const Result = () => {
    const DatafromQuiz=useLocation();
  return (
    <div>
       <div className='result'>
        <p>you have scored {DatafromQuiz.state.score} out of {DatafromQuiz.state.Length}</p>
        <h2>
          {Dascore / Length > 0.8
            ? ` congratulations you got ${(DatafromQuiz.state.score / DatafromQuiz.state.Length) * 100}%`
            : ' study to achieve a better result'}
            
        </h2>
        <Button className="bg-info" onClick={()=>{navigate('/Quiz')}}>retry</Button>
      </div>
    </div>
  )
}

export default Result;
