import React, { useEffect, useState } from "react";
import Quiz from "./Quiz";
import Ques from "./Questions";
const Subjcontext=React.createContext();
const Subject =()=>{
    const [Subject,setSubject]=useState([]);
    const [displayQuiz,setdisp]=useState("none");
    const [displayQuiz2,setdisp2]=useState("block");
    const [Subj,setsubj]=useState("");
    useEffect(()=>{

setSubject(()=>
    {
        return Array.from(new Set(Ques.map((val)=>
        {return val.subject}))
                        )
    }
        )
    },[])
    return (
        <>
        <div style={{backgroundColor:"chocolate",width:"fit-content",margin:"auto",height:"100vh",display:"grid",alignItems:"center",position:"relative"}}>
            <div>
       {Subject.map((val,index)=>{
        return <h2 key={index} style={{display:displayQuiz2,width:"350px",margin:"10px auto",borderRadius:"20px",backgroundColor:"yellowgreen",padding:"1%",textAlign:"center",cursor:"pointer"}}
        onClick={()=>{setsubj(val); setdisp((pre)=>pre==="none"?"block":"none")
        setdisp2((pre)=>pre==="none"?"block":"none")}}>
            {val}</h2>
       })}
       </div>
       <div style={{display:displayQuiz}}> <Quiz subj={Subj}/></div>
       </div>
        </>
    )
    
}
export default {Subjcontext};Subject;
