import React, { useEffect, useState } from 'react'
import Ques from './Questions';
const Quiz = (props) => {
    console.log(props);
    const [Question,setquestion]=useState([]);
    useEffect(()=>{
        setquestion(()=>{
            return Ques.filter((val)=>{
                return val.subject===props.subj;
            })
        })
    },[])
  return (
    <div>
      {Question.map((val)=>{
        return (
        <>
        <h3>{val.question}</h3>
        <ul>
            <label htmlFor='opt1'>
                <input type='radio' name='opt1'/>
            </label>
            <li>{val.option1}</li>
            <li>{val.option2}</li>
            <li>{val.option3}</li>
            <li>{val.option4}</li>
        </ul>
        </>
        )
      })}
    </div>
  )
}

export default Quiz;
