import React, { useEffect, useState } from 'react'
import Ques from './Questions';
const Quiz = (props) => {
    console.log(props);
    const [Question,setquestion]=useState([]);
    useEffect(()=>{
        setquestion(()=>{
            return Ques.filter((val)=>{
                return val.subject===props.subj;
            })
        })
    },[])
  return (
    <div>
      {Question.map((val)=>{
        return (
        <>
        <h3>{val.question}</h3>
        <ul>
            <li>{val.option1}</li>
        </ul>
        </>
        )
      })}
    </div>
  )
}

export default Quiz;
