import React, { useEffect, useState } from 'react'
import Ques from './Questions';
const Quiz = (props) => {
  const [x,setx]=useState(0);
    const [Question,setquestion]=useState([]);
    const [Length,setlength]=useState(0);
    const [score,setscore]=useState(0);
    const [option,setoption]=useState();
    const flag=[];
    useEffect(()=>{
        setquestion(()=>{
            return Ques.filter((val)=>{
                return val.subject===props.subj;
            })
        })
    },[props])
    useEffect(()=>{
      console.log(Question.length);
      setlength(Question.length)
    },[Question])
  return (
    <div>
        <div   style={{overflow:"hidden",display:"flex",width:"350px",margin:"auto"}}>
      {Question.map((val,index)=>{
        if(index!==Length-1){
          return (
        <>
        <div style={{minWidth:"100%",transform:`translateX(-${x}%)`,padding:"1%"}}>
        <h3>{index+1}. &nbsp;{val.question}</h3>
            <p className='opt' style={{backgroundColor:option==="option1"?"green":"white"}} onClick={()=>{setoption("option1"); flag[index]=false}}>(a)&emsp;{val.option1}</p>
            <p className='opt' style={{backgroundColor:option==="option2"?"green":"white"}} onClick={()=>{setoption("option2"); flag[index]=false}}>(b)&emsp;{val.option2}</p>
            <p className='opt' style={{backgroundColor:option==="option3"?"green":"white"}} onClick={()=>{setoption("option3"); flag[index]=false}}>(c)&emsp;{val.option3}</p>
            <p className='opt' style={{backgroundColor:option==="option4"?"green":"white"}} onClick={()=>{setoption("option4"); flag[index]=false}}>(d)&emsp;{val.option4}</p>
            <div style={{display:"flex",justifyContent:"space-between"}}>
              <button className='btn btn-primary' onClick={()=>{setx(x-100)}}>prev</button>
            <button className='btn btn-success'onClick={()=>{
              setx(x+100);
               if(flag[index]!==true && option===val.answer){setscore(x+1);
               flag[index]=true;
               }
               }}>next</button>
            </div>
            </div>
        </>
        )
        }
        else{
        return (
        <>
        <div style={{minWidth:"100%",transform:`translateX(-${x}%)`,padding:"1%"}}>
        <h3>{index+1}. &nbsp;{val.question}</h3>
           <p className='opt' style={{backgroundColor:option==="option1"?"green":"white"}} onClick={()=>{setoption("option1"); flag[index]=false}}>(a)&emsp;{val.option1}</p>
            <p className='opt' style={{backgroundColor:option==="option2"?"green":"white"}} onClick={()=>{setoption("option2"); flag[index]=false}}>(b)&emsp;{val.option2}</p>
            <p className='opt' style={{backgroundColor:option==="option3"?"green":"white"}} onClick={()=>{setoption("option3"); flag[index]=false}}>(c)&emsp;{val.option3}</p>
            <p className='opt' style={{backgroundColor:option==="option4"?"green":"white"}} onClick={()=>{setoption("option4"); flag[index]=false}}>(d)&emsp;{val.option4}</p>
            <button className='btn btn-primary' onClick={()=>{setx(x-100)}}>prev</button>
            <button className='btn btn-success' style={{float:"right"}} onClick={()=>setscore(score+1)}>submit</button>
            </div>
        </>
        )
        }
      })}
      </div>
    </div>
  )
}

export default Quiz;
